import React from 'react';
import Chart from 'react-apexcharts';
import { ApexOptions } from 'apexcharts';

interface Props {
    data: { x: string; y: number }[]; // Adjust based on your data structure
}

const TimeSeriesChart: React.FC<Props> = ({ data }) => {
    // Define options with proper types
    const options: ApexOptions = {
        chart: {
            id: 'time-series-chart',
            type: 'line', // Use string literal for type
            zoom: {
                enabled: false,
            },
        },
        xaxis: {
            type: 'datetime', // Ensure this matches your data
            labels: {
                format: 'dd/MM HH:mm', // Adjust format as needed
            },
        },
        tooltip: {
            x: {
                format: 'dd/MM HH:mm', // Adjust format as needed
            },
        },
    };

    // Ensure series data matches what the chart expects
    const series = [{
        name: 'Visitors',
        data: data, // Your data should be in the format [{ x: 'date', y: value }, ...]
    }];

    return <Chart options={options} series={series} type="line" height={350} />;
};

export default TimeSeriesChart;
