import React from 'react';
import Chart from 'react-apexcharts';

interface SparklineChartProps {
    data: number; // Total number of adults or children
    label: string; // Label for the sparkline
}

const SparklineChart: React.FC<SparklineChartProps> = ({ data, label }) => {
    // Creating an array for the series with a single data point
    const series = [{
        name: label,
        data: [data] // Single data point (total number of adults or children)
    }];

    const options = {
        chart: {
            type: 'line',
            sparkline: {
                enabled: true
            },
            toolbar: {
                show: false // Hide toolbar
            },
            dropShadow: {
                enabled: false // Disable shadow for cleaner look
            }
        },
        stroke: {
            curve: 'smooth',
            width: 2,
        },
        tooltip: {
            fixed: {
                enabled: false
            },
            x: {
                show: false // Disable x-axis tooltip
            },
            y: {
                title: {
                    formatter: () => `${label}:` // Use label for tooltip title
                }
            }
        },
        colors: ['#007bff'], // Set a color for the line (optional)
        grid: {
            show: false // Hide grid lines for cleaner look
        },
        xaxis: {
            show: false // Hide x-axis
        },
        yaxis: {
            show: false // Hide y-axis
        }
    };

    return <Chart options={options} series={series} type="line" height={100} />;
};

export default SparklineChart;
