import React from 'react';
import Chart from 'react-apexcharts';

interface ColumnChartProps {
    data: Record<string, number>; // Key: country name, Value: number of visitors
}

const ColumnChart: React.FC<ColumnChartProps> = ({ data }) => {
    const series = [{
        name: "Visitors",
        data: Object.values(data) // Number of visitors for each country
    }];

    const options = {
        chart: {
            type: 'bar',
            height: 350,
        },
        plotOptions: {
            bar: {
                dataLabels: {
                    position: 'top', // Show data labels on top of bars
                },
            },
        },
        dataLabels: {
            enabled: true,
            formatter: (val: number) => val.toString(),
            offsetY: -20,
            style: {
                fontSize: '12px',
                colors: ['#304758']
            }
        },
        xaxis: {
            categories: Object.keys(data), // Country names
            position: 'top',
            labels: {
                offsetY: -18,
            },
            axisBorder: {
                show: false
            },
            axisTicks: {
                show: false
            },
        },
        yaxis: {
            labels: {
                formatter: (val: number) => val.toFixed(0), // Format y-axis labels
            },
        },
    };

    return <Chart options={options} series={series} type="bar" height={350} />;
};

export default ColumnChart;
