import React from 'react';
import Chart from 'react-apexcharts';
import { ApexOptions } from 'apexcharts';

interface SparklineChartProps {
    data: number[]; // Assuming data is an array of numbers
    label: string;
}

const SparklineChart: React.FC<SparklineChartProps> = ({ data, label }) => {
    const options: ApexOptions = {
        chart: {
            type: 'line', // Specify valid type
            sparkline: {
                enabled: true, // Enable sparkline
            },
            toolbar: {
                show: false, // Hide toolbar if not needed
            },
            dropShadow: {
                enabled: true, // Example property
            },
        },
        stroke: {
            curve: 'smooth', // Type of stroke
            width: 2,
        },
        tooltip: {
            fixed: {
                enabled: false, // Adjust based on your needs
            },
            x: {
                show: true, // Show tooltip for x-axis
            },
            y: {
                formatter: (value: number) => `${value}`, // Tooltip for y-axis
            },
        },
        colors: ['#ff1654'], // Example color
        grid: {
            padding: {
                right: 20, // Example padding
            },
        },
        xaxis: {
            categories: data.map((_, index) => `${index + 1}`), // Placeholder for x-axis categories
            labels: {
                show: true, // Show x-axis labels
            },
        },
        yaxis: {
            show: true, // Show y-axis
        },
    };

    return <Chart options={options} series={[{ name: label, data }]} type="line" height={100} />;
};

export default SparklineChart;
