import React from 'react';
import Chart from 'react-apexcharts';

interface TimeSeriesChartProps {
    data: { [date: string]: number }; // Define the data prop type
}

const TimeSeriesChart: React.FC<TimeSeriesChartProps> = ({ data }) => {
    // Check if data is empty
    if (Object.keys(data).length === 0) {
        return <p>No data available</p>; // Optional: Display a message if there's no data
    }

    const series = [{
        name: "Visitors",
        data: Object.keys(data).map(date => ({ x: new Date(date).getTime(), y: data[date] })) // Convert date string to timestamp
    }];

    const options = {
        chart: {
            id: 'timeseries-chart',
            type: 'line',
            zoom: { enabled: true }
        },
        xaxis: {
            type: 'datetime',
            labels: {
                format: 'MMM dd', // Format the date labels
            }
        },
        tooltip: {
            x: {
                format: 'dd MMM yyyy' // Tooltip date format
            }
        }
    };

    return <Chart options={options} series={series} type="line" height={350} />;
};

export default TimeSeriesChart;
