import React from 'react';
import Chart from 'react-apexcharts';

interface Props {
    data: any; // Adjust the type based on your data structure
}

const TimeSeriesChart: React.FC<Props> = ({ data }) => {
    const options = {
        chart: {
            id: 'time-series-chart',
            type: 'line' as const, // Explicitly define the type as 'line'
            zoom: {
                enabled: false,
            },
        },
        xaxis: {
            type: 'datetime',
            labels: {
                format: 'dd/MM HH:mm', // Adjust format as needed
            },
        },
        tooltip: {
            x: {
                format: 'dd/MM HH:mm', // Adjust format as needed
            },
        },
    };

    const series = [{
        name: 'Visitors',
        data: data, // Ensure this matches your data structure
    }];

    return <Chart options={options} series={series} type="line" height={350} />;
};

export default TimeSeriesChart;
