import React from 'react';
import Chart from 'react-apexcharts';
import { ApexOptions } from 'apexcharts';

interface ColumnChartProps {
    data: number[];
    categories: string[]; // Ensure categories are passed correctly
}

const ColumnChart: React.FC<ColumnChartProps> = ({ data, categories }) => {
    // Check if categories and data are available
    if (!categories || categories.length === 0 || !data || data.length === 0) {
        return <div>No data available</div>; // Handle the case when data is not available
    }

    const options: ApexOptions = {
        chart: {
            type: 'bar',
            height: 350,
            toolbar: {
                show: true,
            },
            animations: {
                enabled: true,
                easing: 'easeinout',
                speed: 400,
                animateGradually: {
                    enabled: true,
                    delay: 150,
                },
                dynamicAnimation: {
                    enabled: true,
                    speed: 350,
                },
            },
        },
        plotOptions: {
            bar: {
                horizontal: false,
                columnWidth: '55%',
            },
        },
        dataLabels: {
            enabled: true,
        },
        title: {
            text: 'Column Chart Example',
            align: 'center',
            style: {
                fontSize: '20px',
                color: '#333',
            },
        },
        xaxis: {
            categories, // Use the categories passed
            title: {
                text: 'Categories',
            },
            labels: {
                style: {
                    colors: '#333',
                    fontSize: '12px',
                },
            },
        },
        yaxis: {
            title: {
                text: 'Values',
            },
        },
        tooltip: {
            shared: true,
            intersect: false,
            x: {
                formatter: (val: number) => `Category: ${categories[val]}`, // Change the parameter type to number
            },
            y: {
                formatter: (val: number) => `Value: ${val}`,
            },
        },
        fill: {
            colors: ['#1E90FF'], // You can customize the bar color
            opacity: 0.8,
        },
        grid: {
            borderColor: '#e7e7e7',
            strokeDashArray: 4,
        },
        responsive: [
            {
                breakpoint: 480,
                options: {
                    legend: {
                        position: 'bottom',
                        horizontalAlign: 'center',
                        floating: false,
                    },
                },
            },
        ],
    };

    const series = [
        {
            name: 'Data',
            data,
        },
    ];

    return (
        <Chart options={options} series={series} type="bar" height={350} />
    );
    
};

export default ColumnChart;
