import React from 'react';
import Chart from 'react-apexcharts';
import { ApexOptions } from 'apexcharts';

interface ColumnChartProps {
    data: number[];
    categories: string[]; // Ensure categories are passed correctly
}

const ColumnChart: React.FC<ColumnChartProps> = ({ data, categories }) => {
    // Check if categories and data are available
    if (!categories || categories.length === 0 || !data || data.length === 0) {
        return <div>No data available</div>; // Handle the case when data is not available
    }

    const options: ApexOptions = {
        chart: {
            type: 'bar',
        },
        xaxis: {
            categories, // Use the categories passed
        },
        // Add other chart options as needed
    };

    return (
        <Chart options={options} series={[{ name: 'Data', data }]} type="bar" height={350} />
    );
};

export default ColumnChart;
