import React, { useState, useEffect } from 'react';
import LineChart from './components/TimeSeriesChart'; // Import LineChart component
import ColumnChart from './components/ColumnChart';  // Import ColumnChart component
import SparklineChart from './components/SparklineChart'; // Import SparklineChart component
import './App.css'; // Import styles
import axios from 'axios'; // Import axios for API requests

const App: React.FC = () => {
    const [startDate, setStartDate] = useState<string>(new Date().toISOString().split('T')[0]); // Set initial startDate to current date
    const [endDate, setEndDate] = useState<string>(new Date().toISOString().split('T')[0]); // Set initial endDate to current date
    const [chartData, setChartData] = useState<any>({}); // Initialize state for chart data
    const [error, setError] = useState<string>(''); // Initialize error state

    // Function to fetch data from API
    const fetchData = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/data', { 
                params: { start: startDate, end: endDate } // Fetch data based on date range
            });
            setChartData(response.data); // Set the received data
            setError(''); // Clear error if data is fetched successfully
        } catch (error) {
            console.error('Error fetching data', error); // Log error in the console
            setError('Failed to fetch data. Please try again.'); // Set error message
        }
    };

    // Fetch data whenever startDate or endDate changes
    useEffect(() => {
        if (startDate && endDate && new Date(startDate) <= new Date(endDate)) {
            fetchData(); // Fetch data if the date range is valid
        } else {
            setError('Invalid date range selected.'); // Set error for invalid date range
        }
    }, [startDate, endDate]);

    return (
        <div className="dashboard-container">
            <div className="header">
                <h1>Hotel Booking Dashboard</h1> {/* Header for the dashboard */}
            </div>
            <div className="date-picker-container">
                <label htmlFor="startDate">Start Date: </label>
                <input 
                    type="date" 
                    id="startDate" 
                    value={startDate} 
                    onChange={e => setStartDate(e.target.value)} 
                />
                <label htmlFor="endDate">End Date: </label>
                <input 
                    type="date" 
                    id="endDate" 
                    value={endDate} 
                    onChange={e => setEndDate(e.target.value)} 
                />
            </div>

            {error && <div className="error-message">{error}</div>} {/* Display error message if any */}

            {chartData && (
                <>
                    <div className="chart-container">
                        <div className="chart-title">Number of Visitors per Day</div>
                        {chartData.timeSeries && <LineChart data={chartData.timeSeries} />} {/* Pass time series data to LineChart */}
                    </div>

                    <div className="chart-container">
                        <div className="chart-title">Number of Visitors per Country</div>
                        {chartData.visitorsByCountry && (
                            <ColumnChart 
                                data={chartData.visitorsByCountry} // Pass the visitors by country data
                            />
                        )}
                    </div>

                    <div className="sparkline-container">
                        <div className="sparkline-chart">
                            <div className="chart-title">Total Adult Visitors</div>
                            <SparklineChart data={chartData.totalAdults ? [chartData.totalAdults] : []} label="Adults" />
                        </div>
                        <div className="sparkline-chart">
                            <div className="chart-title">Total Children Visitors</div>
                            <SparklineChart data={chartData.totalChildren ? [chartData.totalChildren] : []} label="Children" />
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default App;
