import React from 'react';
import Chart from 'react-apexcharts';

interface Props {
    data: number[]; // Adjust the type as per your data
    label: string;
}

const SparklineChart: React.FC<Props> = ({ data, label }) => {
    const options = {
        chart: {
            type: 'line' as const, // Explicitly define the type as 'line'
            sparkline: {
                enabled: true,
            },
            toolbar: {
                show: false,
            },
            dropShadow: {
                enabled: true,
            },
        },
        stroke: {
            curve: 'smooth',
            width: 2,
        },
        tooltip: {
            fixed: {
                enabled: false,
            },
            x: {
                show: false,
            },
            y: {
                title: label,
            },
        },
        colors: ['#008FFB'],
        grid: {
            padding: {
                left: 0,
                right: 0,
            },
        },
        xaxis: {
            type: 'datetime',
            categories: data.map((_, index) => `Day ${index + 1}`), // Adjust as needed
        },
        yaxis: {
            show: false,
        },
    };

    return <Chart options={options} series={[{ name: label, data }]} type="line" height={100} />;
};

export default SparklineChart;
