import React, { useState, useEffect } from 'react';
import LineChart from './components/TimeSeriesChart';
import ColumnChart from './components/ColumnChart';
import SparklineChart from './components/SparklineChart';
import './App.css';
import axios from 'axios';

const App: React.FC = () => {
    const [startDate, setStartDate] = useState<string>(new Date().toISOString().split('T')[0]);
    const [endDate, setEndDate] = useState<string>(new Date().toISOString().split('T')[0]);
    const [chartData, setChartData] = useState<any>({});
    const [error, setError] = useState<string>('');

    const fetchData = async () => {
        try {
          const response = await axios.get('http://localhost:5000/api/data', { params: { start: startDate, end: endDate } });
          setChartData(response.data);
            setError('');
        } catch (error) {
            console.error('Error fetching data', error);
            setError('Failed to fetch data. Please try again.');
        }
    };

    useEffect(() => {
        if (startDate && endDate && new Date(startDate) <= new Date(endDate)) {
            fetchData();
        }
    }, [startDate, endDate]);

    return (
        <div className="dashboard-container">
            <div className="header">
                <h1>Hotel Booking Dashboard</h1>
            </div>
            <div className="date-picker-container">
                <label htmlFor="startDate">Start Date: </label>
                <input type="date" id="startDate" value={startDate} onChange={e => setStartDate(e.target.value)} />
                <label htmlFor="endDate">End Date: </label>
                <input type="date" id="endDate" value={endDate} onChange={e => setEndDate(e.target.value)} />
            </div>

            {error && <div className="error-message">{error}</div>}

            {chartData && (
                <>
                    <div className="chart-container">
                        <div className="chart-title">Number of Visitors per Day</div>
                        <LineChart data={chartData.timeSeries} />
                    </div>

                    <div className="chart-container">
                        <div className="chart-title">Number of Visitors per Country</div>
                        {/* Pass categories to ColumnChart */}
                        <ColumnChart 
                            data={chartData.visitorsByCountry} 
                            categories={chartData.countryNames || []} // Provide a fallback if countryNames doesn't exist
                        />
                    </div>

                    <div className="sparkline-container">
                        <div className="sparkline-chart">
                            <div className="chart-title">Total Adult Visitors</div>
                            <SparklineChart data={chartData.totalAdults ? [chartData.totalAdults] : []} label="Adults" />
                        </div>
                        <div className="sparkline-chart">
                            <div className="chart-title">Total Children Visitors</div>
                            <SparklineChart data={chartData.totalChildren ? [chartData.totalChildren] : []} label="Children" />
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default App;
